import Vue from 'vue';
import Vuex from 'vuex';

Vue.use(Vuex);

const store = new Vuex.Store({
    state: {
        currentPathName: localStorage.getItem("currentPathName") || '默认路径' // 设置初始值
    },
    mutations: {
        setPath(state, pathName) {
            state.currentPathName = pathName;
            localStorage.setItem("currentPathName", pathName); // 同时更新 localStorage
        }
    },
    actions: {
        updateCurrentPathName({ commit }, pathName) {
            commit('setPath', pathName);
        }
    }
});

export default store;
