import Vue from 'vue'
import Vuex from 'vuex'


Vue.use(Vuex)


const store = new Vuex.Store({
    state:{
        currentPathName:''
    },
    mutations: {
        setPath(state, pathName) {
          state.currentPathName = pathName
          localStorage.setItem("currentPathName", pathName)  // 存储在localStorage中
        },
        initializePath(state) {
          state.currentPathName = localStorage.getItem("currentPathName") || ''
        }
      }
})

export default store
